//这是网站计时器
//来源https://anzhiy.cn/posts/sdxhu.html
var now = new Date;
function createtime() {
	var grt = new Date("03/16/2022 00:00:00");
	now.setTime(now.getTime() + 250);
	var days = (now - grt) / 1e3 / 60 / 60 / 24,
		dnum = Math.floor(days),
		hours = (now - grt) / 1e3 / 60 / 60 - 24 * dnum,
		hnum = Math.floor(hours);
	1 == String(hnum).length && (hnum = "0" + hnum);
	var minutes = (now - grt) / 1e3 / 60 - 1440 * dnum - 60 * hnum,
		mnum = Math.floor(minutes);
	1 == String(mnum).length && (mnum = "0" + mnum);

	let currentTimeHtml = "";
	currentTimeHtml = `<span class='textTip'> 本站已运行了 ${dnum} 天</span><span id='runtime'> ${hnum} 小时 ${mnum} 分 </span> <i class='fas fa-hourglass' style='color:#84fab0'></i></br>`,
		document.getElementById("workboard") && (document.getElementById("workboard").innerHTML = currentTimeHtml)
}
setInterval(() => {
	createtime()
}, 700);



